import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import '../styles/LocationMap.css';

// Fix for default marker icons in React-Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
});

function LocationMarker({ position, onPositionChange }) {
  const [markerPosition, setMarkerPosition] = useState(position || [7.2, 80.0]);

  useEffect(() => {
    if (position && Array.isArray(position) && position.length === 2) {
      const [lat, lon] = position;
      if (!isNaN(lat) && !isNaN(lon) && lat >= -90 && lat <= 90 && lon >= -180 && lon <= 180) {
        setMarkerPosition(position);
      }
    }
  }, [position]);

  useMapEvents({
    click(e) {
      const lat = e.latlng.lat;
      const lon = e.latlng.lng;
      
      // Validate coordinates
      if (lat >= -90 && lat <= 90 && lon >= -180 && lon <= 180) {
        const newPos = [lat, lon];
        setMarkerPosition(newPos);
        if (onPositionChange) {
          onPositionChange(lat, lon);
        }
      }
    },
  });

  return (
    <Marker 
      position={markerPosition} 
      draggable={true} 
      eventHandlers={{
        dragend: (e) => {
          const lat = e.target.getLatLng().lat;
          const lon = e.target.getLatLng().lng;
          
          // Validate coordinates
          if (lat >= -90 && lat <= 90 && lon >= -180 && lon <= 180) {
            const newPos = [lat, lon];
            setMarkerPosition(newPos);
            if (onPositionChange) {
              onPositionChange(lat, lon);
            }
          }
        }
      }}
    />
  );
}

function LocationMap({ latitude, longitude, onLocationChange, height = '400px' }) {
  // Ensure proper coordinate order and validation
  const validateCoordinate = (value, min, max, defaultValue) => {
    if (value == null || isNaN(value) || value < min || value > max) {
      return defaultValue;
    }
    return Math.round(value * 10000) / 10000;
  };

  const initialLat = validateCoordinate(latitude, -90, 90, 7.2);
  const initialLon = validateCoordinate(longitude, -180, 180, 80.0);
  
  const [mapPosition, setMapPosition] = useState([initialLat, initialLon]);
  const [selectedLat, setSelectedLat] = useState(initialLat);
  const [selectedLon, setSelectedLon] = useState(initialLon);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [searchError, setSearchError] = useState('');
  const searchTimeoutRef = useRef(null);
  const mapRef = useRef(null);

  useEffect(() => {
    const validLat = validateCoordinate(latitude, -90, 90, 7.2);
    const validLon = validateCoordinate(longitude, -180, 180, 80.0);
    
    // Only update if coordinates have actually changed significantly (more than 0.0001 degrees)
    if (Math.abs(validLat - selectedLat) > 0.0001 || Math.abs(validLon - selectedLon) > 0.0001) {
      setMapPosition([validLat, validLon]);
      setSelectedLat(validLat);
      setSelectedLon(validLon);
    }
  }, [latitude, longitude]);

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (searchTimeoutRef.current) {
        clearTimeout(searchTimeoutRef.current);
      }
    };
  }, []);

  const handlePositionChange = (lat, lon) => {
    // Round coordinates to 4 decimal places (~11m precision)
    const roundedLat = Math.round(lat * 10000) / 10000;
    const roundedLon = Math.round(lon * 10000) / 10000;
    
    setSelectedLat(roundedLat);
    setSelectedLon(roundedLon);
    
    if (onLocationChange) {
      onLocationChange(roundedLat, roundedLon);
    }
  };

  const handleGeolocate = () => {
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser.');
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const lat = position.coords.latitude;
        const lon = position.coords.longitude;
        
        // Validate coordinates from geolocation
        if (lat >= -90 && lat <= 90 && lon >= -180 && lon <= 180) {
          setMapPosition([lat, lon]);
          handlePositionChange(lat, lon);
        } else {
          alert('Invalid coordinates received from geolocation.');
        }
      },
      (error) => {
        let errorMsg = 'Unable to get your location. ';
        switch (error.code) {
          case error.PERMISSION_DENIED:
            errorMsg += 'Location permission denied.';
            break;
          case error.POSITION_UNAVAILABLE:
            errorMsg += 'Location information unavailable.';
            break;
          case error.TIMEOUT:
            errorMsg += 'Location request timed out.';
            break;
          default:
            errorMsg += 'Please select manually on the map.';
        }
        alert(errorMsg);
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 0
      }
    );
  };

  const searchLocation = async (query) => {
    if (!query || query.trim().length < 3) {
      setSearchResults([]);
      setSearchError('');
      return;
    }

    setIsSearching(true);
    setSearchError('');
    
    try {
      // Use OpenStreetMap Nominatim API for geocoding
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?` + 
        `format=json&` +
        `q=${encodeURIComponent(query)}&` +
        `limit=8&` +
        `countrycodes=lk&` +
        `addressdetails=1&` +
        `accept-language=en`,
        {
          headers: {
            'User-Agent': 'SolarAdvisorApp/1.0'
          }
        }
      );
      
      if (response.ok) {
        const data = await response.json();
        
        if (data && data.length > 0) {
          setSearchResults(data);
          setSearchError('');
        } else {
          setSearchResults([]);
          setSearchError('No locations found. Try a different search term.');
        }
      } else {
        setSearchResults([]);
        setSearchError('Search service temporarily unavailable.');
      }
    } catch (error) {
      console.error('Location search error:', error);
      setSearchResults([]);
      setSearchError('Failed to search locations. Please try again.');
    } finally {
      setIsSearching(false);
    }
  };

  const handleSearchChange = (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    setSearchError('');

    // Clear previous timeout
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    // Debounce search - wait 600ms after user stops typing
    if (query.trim().length >= 3) {
      searchTimeoutRef.current = setTimeout(() => {
        searchLocation(query);
      }, 600);
    } else {
      setSearchResults([]);
    }
  };

  const handleSelectLocation = (result) => {
    const lat = parseFloat(result.lat);
    const lon = parseFloat(result.lon);
    
    if (!isNaN(lat) && !isNaN(lon) && lat >= -90 && lat <= 90 && lon >= -180 && lon <= 180) {
      setMapPosition([lat, lon]);
      handlePositionChange(lat, lon);
      
      // Set search query to location name
      const displayName = result.display_name || result.name || '';
      setSearchQuery(displayName.split(',').slice(0, 2).join(','));
      setSearchResults([]);
      setSearchError('');
    }
  };

  return (
    <div className="location-map-container">
      <div className="map-controls">
        <div className="location-search-container">
          <div className="search-input-wrapper">
            <input
              type="text"
              className="location-search-input"
              placeholder="Search for a location in Sri Lanka (e.g., Colombo, Nuwara Eliya)..."
              value={searchQuery}
              onChange={handleSearchChange}
              onFocus={() => {
                if (searchQuery.length >= 3 && searchResults.length === 0 && !searchError) {
                  searchLocation(searchQuery);
                }
              }}
            />
            {isSearching && <span className="search-loading">üîç Searching...</span>}
          </div>
          
          {searchError && (
            <div className="search-error" style={{
              padding: '8px 12px',
              background: '#fff3cd',
              border: '1px solid #ffc107',
              borderRadius: '4px',
              fontSize: '0.9em',
              color: '#856404',
              marginTop: '8px'
            }}>
              {searchError}
            </div>
          )}
          
          {searchResults.length > 0 && (
            <div className="search-results">
              {searchResults.map((result, idx) => (
                <div
                  key={`${result.place_id}-${idx}`}
                  className="search-result-item"
                  onClick={() => handleSelectLocation(result)}
                >
                  <div className="result-name">
                    {result.display_name || result.name}
                  </div>
                  <div className="result-coords">
                    {parseFloat(result.lat).toFixed(4)}¬∞N, {parseFloat(result.lon).toFixed(4)}¬∞E
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
        
        <div className="coordinate-inputs">
          <button 
            type="button" 
            className="geolocate-btn" 
            onClick={handleGeolocate}
            title="Use your current location"
          >
            üìç Use My Location
          </button>
          <div className="coord-display">
            <span title="Latitude">Lat: {selectedLat.toFixed(4)}¬∞</span>
            <span title="Longitude">Lon: {selectedLon.toFixed(4)}¬∞</span>
          </div>
        </div>
      </div>
      
      <div className="map-wrapper" style={{ height }}>
        <MapContainer
          center={mapPosition}
          zoom={10}
          style={{ height: '100%', width: '100%' }}
          scrollWheelZoom={true}
          ref={mapRef}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            maxZoom={19}
          />
          <LocationMarker 
            position={mapPosition} 
            onPositionChange={handlePositionChange}
          />
        </MapContainer>
      </div>
    </div>
  );
}

export default LocationMap;
